#include <iostream>
using namespace std;

// throw -> used for generating an exception
// try -> to check for the exections if generated by the statemnts inside its block
// catch -> to handle the generated exception

void division(int n, int d)
{
    if (d == 0)
        throw 1; // int type of exception
    int result = n / d;
    cout << "Divison = " << result << endl;
}

int main()
{
    // handle the exception
    try // to check for the exceptions genereted by the statements inside it
    {
        division(10, 0);
    }
    catch (int ex) // If exception is generated then the matching catch block gets executed
    {
        cout << "Denominator cannot be 0" << endl;
    }

    cout << "Program finished" << endl;
    return 0;
}